import React, { Component } from "react";
import { Button, Form } from "react-bootstrap";

class Login extends Component {
    constructor(props) {
        super(props);    
        this.state = {
            username: '',
            password: '',
            submitted: false
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    }

    handleSubmit(event) {
        event.preventDefault();
        const { username, password, submitted } = this.state;
        localStorage.setItem('username', username);
        localStorage.setItem('password', username ? password : '');
        this.setState({ submitted: true });
        localStorage.setItem('submitted', submitted);
    }

    render() {
        return(
            <>
                <div className="container mt-5">
                    <div className="row">
                        <div className="col-md-3"></div>
                        <div className="col-md-6 text-left">
                            <h1 className="mb-4">Login</h1>
                            <form onSubmit={this.handleSubmit}>
                                <div className="form-field mb-4">
                                    <input className="form-control" type="text" name="username" onChange={this.handleChange} placeholder="Name"/>
                                </div>
                                <div className="form-field mb-4">
                                    <input className="form-control" type="password" name="password" onChange={this.handleChange} placeholder="Password"/>
                                </div> 
                                <div className="mt-4 text-left">                       
                                    <input className="btn btn-primary" type="submit" value="Submit" />
                                    <a href="register" className="d-inline-block ml-3">Register</a>
                                </div>                                
                            </form>
                        </div>
                        <div className="col-md-3"></div>
                    </div>
                </div>
            </>
        );
    }
}

export default Login;
